import { Link } from "react-router-dom";
import Aff from "./Affiche";
import { useState, useEffect } from "react";
import axios from "axios";
import './App.css';
export default function Search(props) {
  const [movies, setMovies] = useState([]);
  const [value, setValue] = useState('');
  const [selectedMovie, setSelectedMovie] = useState(null);

  // Fetch movies from OMDB API when the search value changes
  useEffect(() => {
    if (value) {
      axios.get(`http://www.omdbapi.com/?apikey=4a78675b&s=${value}`)
        .then(res => setMovies(res.data.Search || []))
        .catch(error => console.error("Error fetching data:", error));
    }
  }, [value]);

  // Handle search submission and set selected movie
  function handleSearchSubmit(e) {
    e.preventDefault();
    setSelectedMovie(props.movies.find(movie => movie.Title.toLowerCase() === value.toLowerCase()) || null);
  }

  return (
<>

      <nav className="navbar navbar-expand-lg " id="header">
        <nav className="container-fluid" id="nav11">
            <Link className="navbar-brand" id="logo" to={'/'}><p><i id="id">M</i>oviesInfo</p></Link>
          <ul className="navbar-nav me-auto mb-0" >
            <li className="nav-item">
              <Link className="nav-link active" id="mmm"  aria-current="page" to={'/'}><p>Home</p></Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link active" id="mmm" aria-current="page" to={'/serie'}><p>Series</p></Link>
            </li>
          </ul>
                  <form className="d-flex" role="search" onSubmit={handleSearchSubmit}>
                  <input class="form-control me-2" type="text" placeholder="Search..." aria-label="Search" onChange={(e) => { setValue(e.target.value) }} />
              <button className="btn btn-subtle" type="submit">
                  <i className="fa-solid fa-magnifying-glass fa-lg"></i>
              </button>
            </form>
      </nav> 
      </nav>
      <div className="container mt-4">
        <div className="row">
          {selectedMovie ? (
            <div className="card" style={{width:"360px"}} id="star">
            <img src={movie.Poster} className="card-img-top" alt={movie.Title} id="mog" />
            <h2>{movie.Genre}</h2>
            <div className="card-body">
            <h6 className="card-title">{movie.Title}</h6>
            <p id="mogg">{movie.Plot}</p>
            Year of Release<h5>{s.Year}</h5>
            <Link to={`/`} className="btn btn-primary">Details</Link>
            </div>
            </div>
          ) : (
            movies.map((movie, i) => (
              <div className="card" style={{width:"360px"}} id="star">
              <img src={movie.Poster} className="card-img-top" alt={movie.Title} id="mog" />
              <h2>{movie.Genre}</h2>
              <div className="card-body">
              <h6 className="card-title">{movie.Title}</h6>
              <p id="mogg">{movie.Plot}</p>
              Year of Release<h5>{movie.Year}</h5>
              <Link to={`/`} className="btn btn-primary">Details</Link>
              </div>
              </div>
            ))
          )}
        </div>
      </div>
      </>
  );
}
