import axios from "axios";
import { useState, useEffect } from "react";
import './App.css'
export default function App() {
  const [series, setSeries] = useState([]);
  const [value, setValue] = useState('');
  useEffect(() => {
    if (value) {
      axios.get(`http://www.omdbapi.com/?apikey=4a78675b&s=${value}&type=series`)
        .then(res => setSeries(res.data.Search || []))
        .catch(error => console.error("Error fetching data:", error));
    }
  }, [value]);

  const handl = (e) => {
    setValue(e.target.value);  
  };

  return (
    <>
    <div>
        <form>
          <input type="text" value={value} onChange={handl} />
        </form>
      </div>
      <div className="con">
        {series.map((se, i) => (
          <div className="card" style={{ width: "250px" }} key={i}>
            <img src={se.Poster} className="card-img-top" alt={se.Title} />
            <div className="card-body">
              <h6 className="card-title">{se.Title}</h6>
              <a href="#" className="btn btn-primary">Details</a>
            </div>
          </div>
        ))}
      </div>
    </>
      
  );
}
