import { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";

export default function Details({ addToWatchlist }) {
  const { title } = useParams();
  const [movie, setMovie] = useState({});

  useEffect(() => {
    axios
      .get(`http://www.omdbapi.com/?apikey=4a78675b&t=${title}&type=series`)
      .then(res => {
        setMovie(res.data);
      });
  }, [title]);

  return (
    <div className="card1">
      <div className="card-thumbnail1">
        <img src={movie.Poster} alt={movie.Title} />
      </div>
      <div className="card-body1">
        <h2 className="card-title1">{movie.Title}</h2>
        <p>
          Year: <span>{movie.Year}</span><br />
          Genre: <span>{movie.Genre}</span>
        </p>
        <p>Story: <span className="card-description1">{movie.Plot}</span></p>
        <div className="eeee">
          <button className="trailer" onClick={() => addToWatchlist(movie)}>Add Watch List</button>&nbsp;&nbsp;
          <button className="trailer">Watched</button>
        </div>
        <Link to={`/`} className="btn btn-light">Home</Link>
      </div>
    </div>
  );
}
