const initialState = {
    user: 'admin',
    password: '1234'
};

const reducer = (state = initialState, action) => {
    switch(action.type) {
        case "SET_USER":
            return { ...state, user: action.payload }; // Properly update user
        case "SET_PASSWORD":
            return { ...state, password: action.payload }; // Properly update password
        default:
            return state;
    }
};

export default reducer;
