import Aff from "./Affiche"
import { useEffect, useState } from "react"
import { useParams } from "react-router-dom"
export default function DetailsSer(props) {
    const { title } = useParams()
    const [text, setText] = useState([])

    useEffect(() => {
        if (props.movie && title) {
            const filteredMovie = props.movie.filter(mov => mov.Title === title)
            setText(filteredMovie) 
        }
    }, [props.movie, title]) 

    return (
        <div>
            {text.length !== 0 
                ? text.map((mov, i) =>{
                    return(
                        <Aff movie={mov} key={i} />
                    )
                })
                : <p>Movie not found.</p>
            }
        </div>
    )
}
