import axios from "axios"
import { useEffect, useState } from "react"
import Aff from "./Afficher"
export default function Rechercherinput() {
const [movies,setmovies]=useState([])
const [movie,setmovie]=useState({})
const [value,setvalue]=useState("")
useEffect(()=>{
  axios.get(`http://localhost/php/api/api.php`)
  .then(res=>{setmovie(res.data)})
})
function rechercher(e) { 
    e.preventDefault()
    setmovie(movies.find((movie)=> movie.Title.toLowerCase()===value.movie.Title.toLowerCase()  ))
 }
  return (
    <>
    <form onSubmit={rechercher}>
        <input type="text" onChange={(e)=>{setvalue(e.target.value)}} className="form-control" />
        <button className="btn btn-primary">rechercher</button>
    </form><br></br>
    <Aff />
    </>
  )
}
