// Action to set the username
export const user = (payload) => {
    return {
        type: "user",
        payload
    };
};

// Action to set the password
export const password = (payload) => {
    return {
        type: "pass",
        payload
    };
};

// Action to verify credentials
export const verifyCredentials = () => {
    return {
        type: "verify"
    };
};
