import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import './App.css'
export default function Search() {
  const [movies, setMovies] = useState([]);
  const [value, setValue] = useState('');
  const [selectedMovie, setSelectedMovie] = useState(null);
  useEffect(() => {
    if (value) {
      axios.get(`http://www.omdbapi.com/?apikey=4a78675b&s=${value}`)
        .then(res => setMovies(res.data.Search || []))
        .catch(error => console.error("Error fetching data:", error));
    }
  }, [value]);
  function Aff(e) {
    e.preventDefault();
    setSelectedMovie(movies.find(movie => movie.Title.toLowerCase() === value.toLowerCase()) || null);
  }

  return (
    <div>
      <nav class="navbar navbar-expand-lg navbar-light bg-light">
  <a class="navbar-brand" href="#">Navbar</a>
  <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
    <span class="navbar-toggler-icon"></span>
  </button>

  <div class="collapse navbar-collapse" id="navbarSupportedContent">
    <ul class="navbar-nav mr-auto">
      <li class="nav-item active">
        <a class="nav-link" href="#">Home <span class="sr-only">(current)</span></a>
      </li>
      <li class="nav-item">
        <a class="nav-link" href="#">Link</a>
      </li>
      <li class="nav-item dropdown">
        <a class="nav-link dropdown-toggle" href="#" id="navbarDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
          Dropdown
        </a>
        <div class="dropdown-menu" aria-labelledby="navbarDropdown">
          <a class="dropdown-item" href="#">Action</a>
          <a class="dropdown-item" href="#">Another action</a>
          <div class="dropdown-divider"></div>
          <a class="dropdown-item" href="#">Something else here</a>
        </div>
      </li>
      <li class="nav-item">
        <a class="nav-link disabled" href="#">Disabled</a>
      </li>
    </ul>
    <form class="form-inline my-2 my-lg-0" onSubmit={Aff}>
      <input class="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" onChange={(e)=>{setValue(e.target.value)}} />
    </form>
  </div>
</nav>
        <div className="con">
          {selectedMovie ? (
                <div className="card" style={{width:"250px"}}>
                  <img src={selectedMovie.Poster} className="card-img-top" alt={selectedMovie.Title} />
                  <div className="card-body">
                    <h6 className="card-title">{selectedMovie.Title}</h6>
                    <a>Details</a>
                  </div>
            </div>
          ) : (
            movies.map((movie, i) => (
              <div key={i} className="con">
                <div className="card" style={{ width: "250px" }}>
                  <img src={movie.Poster} className="card-img-top" alt={movie.Title} />
                  <div className="card-body">
                    <h6 className="card-title">{movie.Title}</h6>
                    <a href="#" className="btn btn-primary">Details</a>
                  </div>
                </div>
              </div>
            ))
          )}
          </div>
          </div>
      
  );
}
