import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import './App.css';

export default function Search(props) {
  const [movies, setMovies] = useState([]);
  const [value, setValue] = useState('');
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // Debounced fetch logic
  useEffect(() => {
    const delayDebounceFn = setTimeout(() => {
      if (value) {
        setIsLoading(true);
        axios.get(`https://www.omdbapi.com/?apikey=4a78675b&s=${value}`)
          .then(res => {
            setMovies(res.data.Search || []);
            setIsLoading(false);
          })
          .catch(err => {
            setError('Error fetching data');
            setIsLoading(false);
          });
      }
    }, 500); // Delay in ms (500ms debounce time)

    return () => clearTimeout(delayDebounceFn); // Cleanup the timeout on next render
  }, [value]);

  // Handle search submission and set selected movie
  function handleSearchSubmit(e) {
    e.preventDefault();
    setSelectedMovie(props.movies.find(movie => movie.Title.toLowerCase() === value.toLowerCase()) || null);
  }

  return (
    <>
      <nav className="navbar navbar-expand-lg " id="header">
        <nav className="container-fluid" id="nav11">
          <Link className="navbar-brand" id="logo" to={'/'}><p><i id="id">M</i>oviesInfo</p></Link>
          <ul className="navbar-nav me-auto mb-0">
            <li className="nav-item">
              <Link className="nav-link active" id="mmm" aria-current="page" to={'/'}><p>Home</p></Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link active" id="mmm" aria-current="page" to={'/serie'}><p>Series</p></Link>
            </li>
          </ul>
          <form className="d-flex" role="search" onSubmit={handleSearchSubmit}>
            <input className="form-control me-2" type="text" placeholder="Search..." aria-label="Search" 
              value={value} 
              onChange={(e) => setValue(e.target.value)} 
            />
            <button className="btn btn-subtle" type="submit">
              <i className="fa-solid fa-magnifying-glass fa-lg"></i>
            </button>
          </form>
        </nav>
      </nav>

      <div className="container mt-4">
        <div className="row">
          {isLoading && <div className="loading">Loading...</div>}
          {error && <div className="error">{error}</div>}

          {selectedMovie ? (
            <div className="card" style={{ width: "360px" }} id="star">
              <img src={selectedMovie.Poster} className="card-img-top" alt={selectedMovie.Title} id="mog" />
              <h2>{selectedMovie.Genre}</h2>
              <div className="card-body">
                <h6 className="card-title">{selectedMovie.Title}</h6>
                <p id="mogg">{selectedMovie.Plot}</p>
                Year of Release <h5>{selectedMovie.Year}</h5>
                <Link to={`/movie/${selectedMovie.Title}`} className="btn btn-primary">Details</Link>
              </div>
            </div>
          ) : (
            movies.map((movie, i) => (
              <div className="card" style={{ width: "360px" }} id="star" key={i}>
                <img src={movie.Poster} className="card-img-top" alt={movie.Title} id="mog" />
                <h2>{movie.Genre}</h2>
                <div className="card-body">
                  <h6 className="card-title">{movie.Title}</h6>
                  <p id="mogg">{movie.Plot}</p>
                  Year of Release <h5>{movie.Year}</h5>
                  <Link to={`/movie/${movie.Title}`} className="btn btn-primary">Details</Link>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </>
  );
}
