import { useEffect, useState } from "react"
import { useNavigate, useParams } from "react-router-dom"
import {useDispatch} from "react-redux"
import { ajouter } from "./Actions"
export default function Product(props) {
  const {id}=useParams()
  const product = props.products.find(function (pro) {  
    return pro.id===parseInt(id)
  })
  
    const [text,settext]=useState('')
    const [quantite,setquantite]=useState(0)
    const dispatch = useDispatch()
    const navigate = useNavigate()
    function buy(e) {  
      e.preventDefault()
      dispatch(ajouter(product,parseInt(quantite)))
      navigate('/')
    }


    
  return (
    <>
      <p>{product.title}</p>
    <form onSubmit={buy}>
        <input type="text" value={quantite} />
        <button  className="btn btn-success">buy</button>
    </form>
    <button onClick={()=>setquantite(parseInt(quantite)+1)}>+</button>
    <button onClick={()=>setquantite(parseInt(quantite)-1)}>-</button>
    </>
  )
}
