import React from 'react';
export default function Watchlist({ watchlist, removeFromWatchlist }) {
  return (
    <div className="container mt-4">
      <h2>Watchlist</h2>
      {watchlist.length === 0 ? (
        <p>No movies in your watchlist.</p>
      ) : (
        <div className='row'>
          {watchlist.map((movie, i) => (
            <div key={i} className="col-3">
              <div className="card" style={{ width: "250px" }}>
                <img src={movie.Poster} className="card-img-top" alt={movie.Title} />
                <div className="card-body">
                  <h6 className="card-title">{movie.Title}</h6>
                  <button onClick={() => removeFromWatchlist(movie)} className="trailer"className="btn btn-danger">Supprimer</button><button to={`/movie/${movie.Title}`} className="trailer">Details</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
      
    </div>
  );
}
