import { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import axios from "axios";
import Home from './Home';
import Search from './Search';
import Serie from './Series';
import Details from './Details';
import './App.css';
import DetailsS from "./Detailsser";
export default function App() {
  const [movies, setMovies] = useState([]);
  const [series, setSeries] = useState([]);0

  useEffect(() => {
    // Fetch movies initially
    axios.get('http://www.omdbapi.com/?apikey=4a78675b&s=star')
      .then(res => setMovies(res.data.Search || []));
  }, []);

  useEffect(() => {
    // Fetch movies initially
    axios.get('http://www.omdbapi.com/?apikey=4a78675b&s=house&type=series')
      .then(res => setSeries(res.data.Search || []));
  }, []);

  return (
    <>
      <BrowserRouter>
        <Search  />
        <Routes>
          <Route path="/" element={<Home movie={movies} />} />
          <Route path="/serie" element={<Serie series={series} />} />
          <Route path="/movie/:title" element={<Details movie={movies} />} />
          <Route path="/serie/:title" element={<DetailsS serie={series} />} />
        </Routes>
      </BrowserRouter>
    </>
  );
}
