const initialestate={
    user:'admin',
    pass:'1234'
}
const reducer=(state= initialestate,action)=>{
    switch(action.type){
        case "incrementer":
            return{...state,user:state.user+1}
        case "decrementer":
            return{...state,user:state.user-1}
        case "reset":
            return{...state,user:0}
        case "incrementerbyuser":
            return{...state,user:state.user=action.payload}
        default:
            return state
    }
}
export default reducer
