import { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import axios from "axios";
import Home from './Home';
import Search from './Search';
import Serie from './Series';
import Details from './Details';
import Nav from './Nav';
import Dserie from "./Detailsser";
import './App.css';

export default function App() {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    axios.get(`http://www.omdbapi.com/?apikey=4a78675b&s=star`)
      .then(res => setMovies(res.data.Search || []))
      .catch(error => console.error("Error fetching data:", error));
  }, []);

  return (
    <BrowserRouter>
      <Nav />
      <Routes>
        <Route path="/" element={<Home movies={movies} />} />
        <Route path="/serie" element={<Serie />} />
        <Route path="/movie/:title" element={<Details />} />
        <Route path="/serie/:title" element={<Dserie />} />
        <Route path="/search" element={<Search movies={movies} />} />
      </Routes>
    </BrowserRouter>
  );
}
