import Aff from "./Afficher";
export default function Thriller(props){
    return(
        <div className="con">
            {
                props.movie.filter((comedy)=>{
                    return comedy.Genre==='Thriller'
                }).map((mov,i)=>{
                    return <Aff movie={mov} key={i} />
                })
            }
        </div>
    )
}
