import { useState, useEffect } from "react";
import axios from "axios";
import './App.css';
import Aff from "./Affiche";
export default function Search() {
  const [movies, setMovies] = useState([]);
  const [value, setValue] = useState('');
  const [selectedMovie, setSelectedMovie] = useState(null);

  // Fetch movies from OMDB API when the search value changes
  useEffect(() => {
      axios.get(`http://www.omdbapi.com/?apikey=4a78675b&s=${value}`)
        .then(res => setMovies(res.data.Search))
  }, [value]);

  // Handle search submission and set selected movie
  function handleSearchSubmit(e) {
    e.preventDefault();
    setSelectedMovie(movies.find(movie => movie.Title.toLowerCase() === value.toLowerCase()) || null);
  }
  return (
<>
      <nav className="box">
        <h1>Search</h1>
        <form onSubmit={handleSearchSubmit}>
          <input type="text" placeholder="Search..." onChange={(e)=>{setValue(e.target.value)}} />
          <input type="submit" value={'Search'} />
        </form>
      </nav>
      <div className="container mt-4">
        <div className="row">
          {selectedMovie ? (
            <Aff />
          ) : (
            movies.map((movie, i) => (
              <Aff movie={movie} key={i}/>
            ))
          )}
        </div>
      </div>
      </>
  );
}
