import React from 'react';
import { Link } from 'react-router-dom';
import './App.css';

export default function Watchlist({ watchlist, removeFromWatchlist }) {
  return (
    <div className="con">
      <h2 className="text-center">Your Watchlist</h2>
      
      {watchlist.length === 0 ? (
        <p className="text-center">No movies in your watchlist.</p>
      ) : (
        <div className=''>
          {watchlist.map((movie, i) => (
              <div className="card" style={{ width: "100%" }} key={i}>
                <img src={movie.Image} className="card-img-top" alt={movie.Title} />
                <div className="card-body">
                  <h6 className="card-title">{movie.Title}</h6>
                  <p>Year of Release: <strong>{movie.Year}</strong></p>
                  <button onClick={() => removeFromWatchlist(movie)} className="btn btn-danger">Supprimer</button>
                  &nbsp;&nbsp;
                  <Link to={`/movie/${movie.Title}`} className="btn btn-primary">Details</Link>
                </div>
              </div>
          ))}
        </div>
      )}
    </div>
  );
}
