import { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import axios from "axios";
import Home from './Home';
import Search from './Search';
import Serie from './Series';
import Details from './Details';
import Dserie from "./Detailsser";
import './App.css';

export default function App() {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  
  useEffect(() => {
    // Fetch movies initially
    axios
      .get('http://www.omdbapi.com/?apikey=4a78675b&s=star')
      .then(res => {
        setMovies(res.data.Search);
        setLoading(false);
      })
      .catch(err => {
        console.error("Error fetching movies:", err);
        setLoading(false);
      });
  }, []);

  return (
    <BrowserRouter>
      <Search />
      {loading && <div>Loading...</div>}
      <Routes>
        <Route path="/" element={<Home movies={movies} />} />
        <Route path="/serie" element={<Serie />} />
        <Route 
          path="/movie/:title" 
          element={<Details />} // Fetch data for the specific movie dynamically
        />
        <Route 
          path="/serie/:title"  
          element={<Dserie />}  // Fetch data for the specific series dynamically
        />
      </Routes>
    </BrowserRouter>
  );
}
