import { useDispatch, useSelector } from "react-redux";
import { user, password, verifyCredentials } from "./actions";  // Import the action creators
import { useState } from "react";
export default function Interface() {
    const dispatch = useDispatch();
    const state = useSelector(state => state); 
    const [isAuthenticated, setIsAuthenticated] = useState(null);
    const [loginError, setLoginError] = useState(null);

    // Handle user input change (both username and password fields)
    const handleUserInput = (event) => {
        const { name, value } = event.target;
        if (name === "user") {
            dispatch(user(value)); // Dispatch the user action
        } else if (name === "pass") {
            dispatch(password(value)); // Dispatch the password action
        }
    };

    // Verify credentials action when the button is clicked
    const handleVerify = () => {
        // Dispatch the verifyCredentials action
        dispatch(verifyCredentials());

        // After dispatching, check if the user is authenticated
        if (state.isAuthenticated) {
            setIsAuthenticated(true);
            setLoginError(null); // Clear error if authentication is successful
        } else {
            setIsAuthenticated(false);
            setLoginError(state.loginError || "Invalid credentials"); // Display error if login fails
        }
    };

    return (
        <div>
            <input
                type="text"
                name="user"
                value={state.user}
                onChange={handleUserInput}
                placeholder="Enter username"
            />
            <input
                type="password"
                name="pass"
                value={state.password}
                onChange={handleUserInput}
                placeholder="Enter password"
            />
            <button onClick={handleVerify}>Verify</button>

            {isAuthenticated !== null && (
                <div>
                    {isAuthenticated ? <p>Authenticated: true</p> : <p>Authenticated: false</p>}
                </div>
            )}

            {loginError && <p style={{ color: 'red' }}>{loginError}</p>}
        </div>
    );
}
