import axios from "axios";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import './App.css';
import { Link } from "react-router-dom";
export default function Serie() {
  const [series, setSeries] = useState([]);
  const { title } = useParams()
    const [text, setText] = useState([])

    useEffect(() => {
        if (props.movie && title) {
            const filteredMovie = props.movie.filter(mov => mov.Title === title)
            setText(filteredMovie) 
        }
    }, [props.movie, title]) 

  useEffect(() => {
    axios.get(`http://www.omdbapi.com/?apikey=4a78675b&s=house&type=series`)
      .then(res => {
        if (res.data.Search) {
          setSeries(res.data.Search); 
        }
      })
      .catch(error => {
        console.error("Error fetching data:", error); 
      });
  }, []);

  return (
    <>
      <div className="con">
        {series.map((se, i) => (
          <div className="card" style={{ width: "250px" }} key={i}>
            <img src={se.Poster} className="card-img-top" alt={se.Title} />
            <div className="card-body">
              <h6 className="card-title">{se.Title}</h6>
              <Link to={`/movie/${se.Poster}`} className="">Details</Link>
            </div>
          </div>
        ))}
      </div>
    </>
  );
}
