const initialState = {
    user: 'admin',
    password: '1234'
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case "user":
            return { ...state, user: action.payload };
        case "pass":
            return { ...state, password: action.payload };
        default:
            return state;
    }
};

// Function to check if user and password are correct
const verifyCredentials = (state) => {
    if (state.user === 'admin' && state.password === '1234') {
        return true;
    } else {
        return false;
    }
};


const currentState = {
    user: 'admin',
    password: '1234'
};

console.log(verifyCredentials(currentState));
