import Home from './Home'
import axios from "axios";
import { BrowserRouter,Routes,Route } from "react-router-dom";
import { useState,useEffect } from "react";
export default function App() {
    const [movies,setmovies]=useState([])
    const [searchV,setsearchV]=useState('')
    useEffect(()=>{
        axios.get('http://www.omdbapi.com/?apikey=4a78675b&s=avengers')
        .then(res=>setmovies(res.data.Search))
    },[])
    function rechercher(e) { 
        e.preventDefault()
        setmovies(movies.filter(function (mov) { return mov.Title===searchV  }))
     }

return(
    <BrowserRouter>
        <form onSubmit={rechercher}>
            <input type='text' onChange={(e)=>{setsearchV(e.target.value)}} />
        </form>
        <div>
        <h5 className="card-title">{movies.Title}</h5>
        </div>
        <Routes>
            <Route path={'/'} element={<Home movie={movies} />} />
        </Routes>
    </BrowserRouter>
)
}
