// src/Details.js
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

export default function Details({ addToWatchlist }) {
  const { title } = useParams();
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    axios.get(`http://www.omdbapi.com/?apikey=4a78675b&t=${title}`)
      .then(res => setMovie(res.data))
      .catch(error => console.error("Error fetching data:", error));
  }, [title]);

  return (
    <div className="container mt-4">
      {movie ? (
        <div className="card">
          <div className="card-header">
            <h2>{movie.Title}</h2>
          </div>
          <div className="card-body">
            <img src={movie.Poster} alt={movie.Title} className="img-fluid mb-4" />
            <p><strong>Year:</strong> {movie.Year}</p>
            <p><strong>Genre:</strong> {movie.Genre}</p>
            <p><strong>Plot:</strong> {movie.Plot}</p>
            <button className="btn btn-primary" onClick={() => addToWatchlist(movie)}>Add to Watchlist</button>
          </div>
          <div className="card-footer">
            <a href="/" className="btn btn-primary">Back to Home</a>
          </div>
        </div>
      ) : (
        <p>Movie not found</p>
      )}
    </div>
  );
}
