import { useEffect, useState } from "react"
import axios from "axios";
export default function Rechercherinput(props) {
const [value,setvalue]=useState("")
const [movie,setmovie]=useState({})
useEffect(() => {
    axios.get(`http://localhost/php/api/api.php`)
      .then(res => setmovie(res.data))
  }, [movie]);
function rechercher(e) { 
    e.preventDefault()
    setvalue(props.movie.find((mov)=> { return mov.title===value  }))
 }
  return (
    <>
    <form onSubmit={rechercher}>
    <input type="text" placeholder="Search..." onChange={(e)=>{setvalue(e.target.value)}} />
        <button className="">rechercher</button>
    </form><br></br>
    <div className="card" style={{width:"18rem"}}>
    <img src={props.movie.image} className="card-img-top" alt={props.movie.title} />
    <div className="card-body">
    <h5 className="card-title">{props.movie.title}</h5>
    <p className="card-text">{props.movie.description}</p>
    <a href="#" className="btn btn-primary">avoir plus</a>
    </div>
    </div>
    </>
  )
}
