import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Link } from 'react-router-dom';
export default function Details(props) {
  const { Title } = useParams();
  const [movie, setMovie] = useState('');

  useEffect(() => {
    const foundMovie = props.movie.find((m) => m.Title === Title.toLowerCase());
  }, [Title]);

  return (
    <div className="container mt-4">
      {movie ? (
        <div className="card1">
        <div className="card22">
          <img src={movie.Image} alt={movie.Title} />
        </div>
        <div className="card-body1">
          <h2 className="card-title1">{movie.Title}</h2>
          <p>
            Year: <span>{movie.Year}</span><br />
            Genre: <span>{movie.Genre}</span>
          </p>
          <p>Story: <span className="card-description1">{movie.Description}</span></p>
          <div className="eeee">
            <button className="trailer" onClick={() => addToWatchlist(movie)}>Add Watch List</button>
          </div>
          <Link to={`/`} className="btn btn-light" id="click">Retour</Link>
        </div>
      </div>
      ) : (
        <p>Movie not found</p>
      )}
    </div>
  );
}
