export default function Index(){
  const [movies, setMovies] = useState([]);
  const [value, setValue] = useState("");
  const [selectedMovies, setSelectedMovies] = useState([]);

  useEffect(() => {
    if (value) {
      axios
        .get(`http://www.omdbapi.com/?apikey=4a78675b&s=${value}`)
        .then((res) => setMovies(res.data.Search))
        .catch((error) => console.error("Error fetching data:", error));
    }
  }, [value]);
    return(
        <div>
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
  <div className="container-fluid">
    <a className="navbar-brand" href="#">MoviesInfo</a>
    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span className="navbar-toggler-icon"></span>
    </button>
    <div className="collapse navbar-collapse" id="navbarSupportedContent">
      <ul className="navbar-nav me-auto mb-2 mb-lg-0">
        <li className="nav-item">
          <a className="nav-link active" aria-current="page" href="#">Home</a>
        </li>
      </ul>
      {
        <>
        <form className="input-group mb-3" onSubmit={rechercher}>
    <input type="text" className="form-control" placeholder="Enter Movvie" aria-label="Recipient's username" aria-describedby="basic-addon2"  onChange={(e)=>setValue(e.target.value)} />
    <div class="input-group-append">
      <button className="btn btn-outline-secondary" type="button">Button</button>
    </div>
  </form>
        {selectedMovies.length > 0 ? (
          selectedMovies.map((movie,i) => (
            <div key={i}>
              <p>{movie.Title}</p>
              <img src={movie.Poster} alt={movie.Title} />
            </div>
          ))
        ) : (
          <p>{}</p>
        )}
      </>
      }
    </div>
  </div>
</nav>
        </div>
    )
