import { Link } from "react-router-dom";
import Aff from "./Affichage";
import { useState, useEffect } from "react";
import axios from "axios";
import './App.css';
export default function Search(props) {
  const [movies, setMovies] = useState([]);
  const [value, setValue] = useState('');
  const [selectedMovie, setSelectedMovie] = useState(null);

  // Fetch movies from OMDB API when the search value changes
  useEffect(() => {
    if (value) {
      axios.get(`http://www.omdbapi.com/?apikey=4a78675b&s=${value}&type=movie`)
        .then(res => setMovies(res.data.Search || []))
        .catch(error => console.error("Error fetching data:", error));
    }
  }, [value]);

  // Handle search submission and set selected movie
  function handleSearchSubmit(e) {
    e.preventDefault();
    setSelectedMovie(props.movies.find(movie => movie.Title.toLowerCase() === value.toLowerCase()) || null);
  }

  return (
  <>
      <div className="container-fluid">
        <a className="navbar-brand" href="#">MoviesInfo</a>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
              <Link className="nav-link" to={'/'}>Home <span className="sr-only">(current)</span></Link>
              <Link className="nav-link" to={'/serie'}>Series</Link>
          <form className="d-flex" onSubmit={handleSearchSubmit}>
            <input 
              className="form-control me-2" 
              type="text" placeholder="Search" aria-label="Search" onChange={(e) => { setValue(e.target.value) }} />
          </form>
        </div>
        </div>
      <div className="container mt-4">
        <div className="row">
          {selectedMovie ? (
            <div className="con">
              <Aff movie={selectedMovie} />
            </div>
          ) : (
            movies.map((movie, i) => (
                <Aff movie={movie} key={i} />
            ))
          )}
        </div>
        </div>
    </>
  );
}
<nav class="navbar">
  <div class="container-fluid">
    <a class="navbar-brand">
    <img src="/images/bootstrap-logo.svg" width="36" alt="Logo" />
    </a>
    <form class="d-flex" role="search">
      <input class="form-control me-2" type="search" 
      placeholder="Search..." 
      aria-label="Search" />
      <button class="btn btn-subtle" type="submit">
      <i class="fa-solid fa-magnifying-glass fa-lg"></i>
      </button>
    </form>
  </div>
</nav>
