const initialestate={
    user:'admin',
    pass:'1234'
}
const reducer = (state = initialestate, action) => {
    switch (action.type) {
        case "user":
            return { ...state,user: action.payload };
        case "pass":
            return { ...state, password: action.payload };
        case "verify":
            const correctCredentials = { user: 'admin', password: '1234' };

            // Check if the user and password match
            if (state.user === correctCredentials.user && state.password === correctCredentials.password) {
                return {
                    ...state,
                    isAuthenticated: true,
                    loginError: null // Reset error message if login is successful
                };
            }
        default:
            return state
    }
    
}
export default reducer
