import React from 'react';
import Aff from './Affiche';
import './App.css'; // Ensure this import is at the top to apply your styles

export default function Watchlist({ watchlist,removeFromWatchlist}) {
  return (
    <div className='container mt-4' id='con'>
      
      {watchlist.length === 0 ? (
        <p className='text-center'>No movies in your watchlist.</p>
      ) : (
        <div className="row">
           {watchlist.map((movie, i) => (
             <div key={i} className="col-3"> <div className="card" style={{ width: "250px" }}> <img src={movie.Poster} className="card-img-top" alt={movie.Title} /> <div className="card-body"> <h6 className="card-title">{movie.Title}</h6> <button onClick={() => removeFromWatchlist(movie)} className="btn btn-danger">Supprimer</button> </div> </div> </div> ))} </div>
      )}
      <div className='text-center'>
      </div>
    </div>
  );
}
