import { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import './App.css'
export default function Details({ addToWatchlist }) {
  const { title } = useParams();
  const [movie, setMovie] = useState({});

  useEffect(() => {
    axios
      .get(`http://www.omdbapi.com/?apikey=4a78675b&t=${title}`)
      .then(res => {
        setMovie(res.data);
      });
  }, [title]);

  return (
    <div className="card1">
      <div className="card22">
        <img src={movie.Poster} alt={movie.Title} />
      </div>
      <div className="card-body1">
        <h2 className="card-title1">{movie.Title}</h2>
        <p>
          Year: <span>{movie.Year}</span><br />
          Genre: <span>{movie.Genre}</span>
        </p>
        <p>Story: <span className="card-description1">{movie.Plot}</span></p>
        <div className="eeee">
          <button className="trailer" onClick={() => addToWatchlist(movie)}>Add Watch List</button>
        </div>
        <Link to={`/`} className="btn btn-light" id="click">Retour</Link>
      </div>
    </div>
  );
}
