import React from 'react';
import { Link } from "react-router-dom";
export default function Watchlist({ watchlist }) {
  return (
    <div className="container mt-4">
      <h2>Watchlist</h2>
      {watchlist.length === 0 ? (
        <p>No movies in your watchlist.</p>
      ) : (
        <div className="row">
          {watchlist.map((movie, i) => (
            <div className="card" style={{width:"250px"}} key={i}>
            <img src={movie.Poster} alt={movie.Title} />
            <div className="card-body">
            <h6 className="card-title">{movie.Title}</h6>
            Year of Release<h5>{movie.Year}</h5><Link onClick={() => removeFromWatchlist(movie)} className="btn btn-danger">Supprimer</Link>
            <Link to={`/movie/${movie.Title}`} className="">Details</Link>
            </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
