import axios from "axios";
import { useState, useEffect } from "react";

export default function Search(props) {
  const [movies, setMovies] = useState([]);
  const [value, setValue] = useState("");
  const [selectedMovie, setSelectedMovie] = useState(null);

  useEffect(() => {
    axios.get('http://localhost/php/api/api.php')
      .then(res => setMovies(res.data))
      .catch(error => console.error("Error fetching data:", error));
  }, []);

  function rechercher(e) { 
    e.preventDefault();
    const movie = movies.find(mov => mov.Title.toLowerCase() === value.toLowerCase());
    setSelectedMovie(movie);
  }

  return (
    <>
      <form onSubmit={rechercher}>
        <input 
          type="text" 
          onChange={(e) => setValue(e.target.value)} 
          className="form-control" 
          placeholder="Enter movie title" 
        />
        <button className="btn btn-primary" type="submit">Rechercher</button>
      </form>
      <br />

      {selectedMovie ? (
        <div className="card" style={{ width: "18rem" }}>
          <div className="card-body">
            <h5 className="card-title">{selectedMovie.Title}</h5>
            <p className="card-text">{selectedMovie.Description}</p>
          </div>
        </div>
      ) : (
        <p>No movie selected or movie not found.</p>
      )}
    </>
  );
}
