import { useState, useEffect } from "react";
import axios from "axios";
import './App.css';
import Aff from "./Affiche";
export default function Search() {
  const [movies, setMovies] = useState([]);
  const [value, setValue] = useState('');
  const [selectedMovie, setSelectedMovie] = useState(null);

  // Fetch movies from OMDB API when the search value changes
  useEffect(() => {
    if (value) {
      axios.get(`http://www.omdbapi.com/?apikey=4a78675b&s=${value}`)
        .then(res => setMovies(res.data.Search || []))
        .catch(error => console.error("Error fetching data:", error));
    }
  }, [value]);
  return (
<>
      <nav>
          <input type="text" onChange={(e)=>{selectedMovie(e.target.value)}}/>
      </nav> 
      <div className="container mt-4">
        <div className="row">
          {selectedMovie ? (
            <Aff />
          ) : (
            movies.map((movie, i) => (
              <Aff movie={movie} key={i}/>
            ))
          )}
        </div>
      </div>
      </>
  );
}
