import { useDispatch,useSelector } from "react-redux";
import { incrementer,decrementer,resert,incrementerbynumber } from "./action";
import { useState } from "react";
export default function Interface(){
    const number=useSelector(data=>data.number)
    const disptch=useDispatch()
    const [n,setn]=useState(0)
    return(
        <div>
            <button onClick={()=>{disptch(incrementer())}}>Incrementer</button>
            <button onClick={()=>{disptch(decrementer())}}>Decrementer</button>
            <button onClick={()=>{disptch(resert())}}>Reset</button>
            <input type="text" onChange={(e)=>{setn(e.target.value)}} />
            <button onClick={()=>{disptch(incrementerbynumber(parseInt(n)))}}>Incrementer By Number</button>
            <h2>{number}</h2>
        </div>
    )
}
