import Affichage from "./Affiche"
import axios from "axios"
import { useState,useEffect } from "react"
// export default function Recipes(props){
//     return(
//         props.recipes.map(function(rec,i){
//             return <Affichage recipes={rec} key={i}/>
//         })
//     )
// }
export default function Rechercherinput(props) {
const [recipes,setrecipes]=useState({})
const [value,setvalue]=useState("")
useEffect(()=>{
    axios.get('https://dummyjson.com/recipes')
    .then(res=>setrecipes(res.data.recipes))
},[])
function rechercher(e) { 
    e.preventDefault()
    setrecipes(props.recipes.find(function (rec) { return rec.name===value  }))
 }
  return (
    <>
    <form onSubmit={rechercher}>
        <input type="text" onChange={(e)=>{setvalue(e.target.value)}} className="form-control" />
        <button className="btn btn-primary">rechercher</button>
    </form><br></br>
    <h5 className="card-title">{props.recipes.name}</h5>
    </>
  )
}
